<script src="https://api-maps.yandex.ru/2.1/?lang=ru_RU" type="text/javascript"></script>
<script>
	ymaps.ready(function() {
		var coords = \'59.962528, 30.288159\';
		if (coords != \'\') {
		    coords = coords.replace(/\\s/gi,\'\');
		    coords = coords.split(\',\');
		    if (!coords.length) {
		        return false;
		    }
		}

		var myMap = new ymaps.Map(\'map\', {
			center: coords,
			zoom: 16,
			controls: [] //\'zoomControl\'
		}, {
			//searchControlProvider: \'yandex#search\'
		});
		placemarkOffice = new ymaps.Placemark(coords, {
			hintContent: \'Офис компании\',
			balloonContent: \'Офис: г. Санкт-Петербург, Большая Зеленина, д.18\',
		}, {
			iconLayout: \'default#image\',
            // Своё изображение иконки метки.
            iconImageHref: \'assets/img/marker.png\',
            // Размеры метки.
            iconImageSize: [50, 74],
            // Смещение левого верхнего угла иконки относительно
            // её "ножки" (точки привязки).
            iconImageOffset: [-25, -74]
		});

		// Offset
		var position = myMap.getGlobalPixelCenter();
		var map_elem = document.getElementById(\'map\');
		var viewport_width = map_elem.clientWidth;
		var map_height = map_elem.clientHeight;

		// vw >= 1200
		var map_offset_x = -348;
		var map_offset_y = 0;

		// vw < 1200
		if (viewport_width < 1200) {
			map_offset_x = 0;
			map_offset_y = Math.ceil(map_height * 0.33) * -1;
		}

		myMap.setGlobalPixelCenter([ position[0] + map_offset_x , position[1] + map_offset_y ]);
	  
		myMap.geoObjects.add(placemarkOffice);
		myMap.behaviors.disable(\'scrollZoom\');
	});
</script>',
